#!/usr/bin/env python3\n\nfrom launch import LaunchDescription\nfrom launch_ros.actions import Node\nfrom launch.substitutions import PathJoinSubstitution\nfrom launch.launch_description_sources import PythonLaunchDescriptionSource\nfrom launch.actions import IncludeLaunchDescription\nfrom ament_index_python.packages import get_package_share_directory\n\n\ndef generate_launch_description():\n    # Get package directory\n    pkg_dir = get_package_share_directory('student_assignment_02')\n    \n    # Config file path\n    mapper_params_file = PathJoinSubstitution([\n        pkg_dir,\n        'config',\n        'mapper_params_online_async.yaml'\n    ])\n    \n    ld = LaunchDescription()\n    \n    # Static transform publisher (laser -> base_link)\n    static_tf = Node(\n        package='tf2_ros',\n        executable='static_transform_publisher',\n        arguments=['--x', '0.0', '--y', '0.0', '--z', '-0.15',\n                   '--roll', '0.0', '--pitch', '0.0', '--yaw', '0.0',\n                   '--frame-id', 'base_link', '--child-frame-id', 'laser'],\n        output='screen'\n    )\n    ld.add_action(static_tf)\n    \n    # SLAM Toolbox - Online Async mode\n    slam_node = Node(\n        package='slam_toolbox',\n        executable='async_slam_toolbox_node',\n        name='slam_toolbox',\n        output='screen',\n        parameters=[mapper_params_file]\n    )\n    ld.add_action(slam_node)\n    \n    return ld\n